<?phpnamespace controllers;use models\Catalog;use models\model;//// Конттроллер страницы чтения.////include_once('models/model.php');class C_Page extends C_Base{    // Конструктор.    //    public function action_index()    {        $model = new Catalog();        $this->title .= 'Каталог';        $category = $model->getCategory();        $this->content = $this->Template('category', ['category' => $category]);    }    public function action_good()    {        $this->title = 'Товар';        $model = new Catalog();        $good = $model->getGood();        $this->content = $this->Template('good', ['good' => $good]);    }    public function action_goods()    {        $this->title = 'Товары';        $model = new Catalog();        $goods = $model->getGoods();        $this->content = $this->Template('goods', ['goods' => $goods, 'category'=>$category]);    }}